mapper.select:
SELECT wordbook_id AS wordbookId, wordbook_code AS wordbookCode, wordbook_desc AS wordbookDesc, wordbook_group_code AS wordbookGroupCode , order_num AS orderNum
ORDER BY order_num
LIMIT ${start}, ${end}

mapper.sql:
FROM t_service_wordbook WHERE
wordbook_group_code = #{wordbookGroupCode}
AND wordbook_desc like ('%${wordbookDesc}%')
