mapper.select:
SELECT  FROM t_ucenter_ms_organization
WHERE 1 = 1

    AND null = #{null}

SELECT id AS id, name AS text,parent_id AS parentId, id AS wxOrgId FROM t_ucenter_ms_organization where is_disable = 0
AND parent_id =#{parentId}
AND id =#{userOrgId}
AND id LIKE CONCAT(#{userOrgId}, '%')
ORDER BY parent_id

SELECT COUNT(1) FROM t_ucenter_ms_organization WHERE 1=1

    AND parent_id = #{parentId}


    AND name = #{name}

SELECT COUNT(1) FROM t_ucenter_ms_organization
WHERE id LIKE CONCAT(#{organizationId}, '%') AND id != #{organizationId}

    AND is_disable = #{isDisable}
