mapper.select:
SELECT menu_id AS menuId, menu_name AS menuName, father_menu_id AS fatherMenuId, menu_url AS menuUrl, namespace, menu_type AS menuType, is_enable AS isEnable, ( CASE is_enable WHEN '0' THEN '启用' WHEN '1' THEN '禁用' END ) AS state, image, server_name_id AS serverNameId, namespace, check_help_url AS checkHelpUrl, system_id AS systemId FROM t_ucenter_ms_menu
AND menu_type LIKE CONCAT('%',#{menuType},'%')
AND father_menu_id=#{menuId}
LIMIT ${start},${end}

SELECT COUNT(1) FROM `t_ucenter_ms_menu`
AND menu_type LIKE CONCAT('%',#{menuType},'%')
AND father_menu_id=#{menuId}
