mapper.select:
SELECT user_name AS userName, is_enable AS isEnable, user_id AS userId, user_login_name AS userLoginName, is_admin AS isAdmin, password AS password, mobile, email, sex, organization_id AS organizationId, create_user AS createUser, create_time AS createTime, update_user AS updateUser, update_time AS updateTime, group_code AS groupCode, is_tuya_user AS isTuYaUser, u_id AS uId, country_code AS countryCode FROM t_ucenter_ms_user
AND (user_login_name = #{userLoginName} or mobile = #{userLoginName} or email = #{userLoginName})
AND password = #{password}
AND group_code = #{groupCode}
AND is_tuya_user = #{isTuYaUser}
AND is_app_user = #{isAppUser}
AND is_enable=1 AND is_delete=0

SELECT m.menu_name as menuName, m.menu_id as menuId, m.menu_url as menuUrl , m.father_menu_id as fatherMenuId FROM t_sett_ms_menu m WHERE m.menu_id = #{fatherMenuId}
AND m.menu_type LIKE CONCAT('%',#{menuType},'%')
ORDER BY m.father_menu_id,m.order_index

SELECT user_name AS userName, user_id AS userId, user_login_name AS userLoginName, password,header, email,is_admin AS isAdmin, is_enable AS isEnable, organization_id AS organizationId FROM t_ucenter_ms_user
AND (user_login_name = #{userLoginName} or mobile = #{userLoginName} or email = #{userLoginName})
AND password = #{password}
AND user_id = #{userId}

