mapper.select:
SELECT permission_name as permissionName, permission_id as permissionId, method as method, menu_id as menuId, permission_type as permissionType, ( CASE is_disable WHEN '0' THEN '启用' WHEN '1' THEN '禁用' end ) AS state FROM t_ucenter_ms_menu_permission WHERE 1=1
AND menu_id = #{menuId}
LIMIT ${start},${end}

SELECT permission_name AS permissionName, permission_id AS permissionId, method AS account, permission_type AS permissionType, ( CASE is_disable WHEN '0' THEN '启用' WHEN '1' THEN '禁用' end ) AS state FROM t_ucenter_ms_menu_permission WHERE 1=1
AND permission_type = #{permissionType}
AND permission_type is NULL
AND menu_id = #{menuId}
AND menu_id is NULL

SELECT COUNT(*) FROM `t_ucenter_ms_menu_permission` WHERE
menu_id = #{menuId}
menu_id is NULL

SELECT count(1)
FROM t_ucenter_ms_menu_permission_url_rela
WHERE permission_id = #{permissionId}

mapper.update:
UPDATE t_ucenter_ms_menu_permission_url_rela
SET
url=#{url}
WHERE permission_id=#{permissionId}
AND url=#{oldUrl}

mapper.delete:
DELETE FROM
t_ucenter_ms_menu_permission_url_rela
WHERE permission_id=#{permissionId}
AND url=#{url}
