mapper.sql:
WHERE fi.create_user = #{userId}

    AND fi.title LIKE '%${processTitle}%'


    AND fjx.name LIKE '%${processName}%'

mapper.select:
SELECT fth.task_id, fi.title processTitle, fjx.name processName, fth.title taskName, fi.status processStatus, fi.create_user createUser, fth.task_finish_time taskFinishTime, fth.use_time useTime, fth.`result` useStatus FROM t_flow_task_history fth LEFT JOIN t_flow_instance fi ON fi.activiti_process_instance_id = fth.instance_id LEFT JOIN t_flow_jbpm_xml fjx ON fjx.id = fi.form_pkey
ORDER BY task_finish_time DESC
LIMIT ${start}, ${end}
