mapper.select:
SELECT `ID_` as taskId, `NAME_` as taskName, `OWNER_` as taskOwner, `ASSIGNEE_` as assignee, `PRIORITY_` as priority, DATE_FORMAT(`CREATE_TIME_`,'%Y-%m-%d %H:%i:%s') taskCreateTime, `DUE_DATE_` dueDate, `CATEGORY_` category, `SUSPENSION_STATE_` suspensionSate, `FORM_KEY_` formKey, `id` as fhsInstanceId, `title`, `create_user`, i.create_time instanceCreateTime, `activiti_process_instance_id` as activitiInstanceId FROM `act_ru_task` t LEFT JOIN t_flow_instance i ON t.PROC_INST_ID_ = i.activiti_process_instance_id
ORDER BY CREATE_TIME_ DESC
LIMIT ${start}, ${end}

SELECT DISTINCT `ID_` as taskId, `NAME_` as taskName, `OWNER_` as taskOwner, `ASSIGNEE_` as assignee, `PRIORITY_` as priority, DATE_FORMAT(`CREATE_TIME_`,'%Y-%m-%d %H:%i:%s') taskCreateTime, `DUE_DATE_` dueDate, `CATEGORY_` category, `SUSPENSION_STATE_` suspensionSate, `FORM_KEY_` formKey, `id` as fhsInstanceId, `title`, `create_user`, i.create_time instanceCreateTime, `activiti_process_instance_id` as activitiInstanceId
ORDER BY RES1.CREATE_TIME_ DESC
LIMIT ${start}, ${end}
